import React, { useState } from 'react';

import {useDispatch} from 'react-redux';
import { savePaymentMethod } from '../actions/cartActions';
import CheckoutSteps from '../components/CheckoutSteps';
export default function PaymentMethodScreen(props) {
const [PaymentMethod, setPaymentMethod] = useState('Paypal');
 const dispatch = useDispatch();
const submitHandler = (e) => {
    e.preventDefault();
    dispatch(savePaymentMethod(PaymentMethod));
    props.history.push('/placeorder')
};

    return (
        <div>
            <CheckoutSteps step1 step2 step3></CheckoutSteps>
            <form className="form" onSubmit={submitHandler}>
                <div>
                <h1>Méthode de paiement</h1>
                </div>
<div>
<div> 
<input type ="radio" id="paypal"  value="PayPal" name="paymentMethod" 
required checked onChange={(e) => setPaymentMethod(e.target.value)}></input>
<label htmlFor="paypal">PayPal</label>
</div>
</div>
<div>
<input
type="radio"
id="Stripe"
value="Stripe"
name="PaymentMethod"
required
checked
onChange={(e)=> setPaymentMethod(e.target.value)}>

</input>

<label htmlFor="Stripe">Paiement à la livraison</label>
</div>
<div>
 <button className="primary"  type="submit">Suivant</button> 
</div>
</form>
</div>
    )
}
