import { USER_SIGININ_FAIL, USER_SIGININ_REQUEST, USER_SIGININ_SUCCESS, USER_SIGNOUT } from "../constants/userConstants";

export const userSigninReducer=(state = {}, action ) => {
switch(action.type){
case USER_SIGININ_REQUEST: 
return {loading: true} ; 
    case USER_SIGININ_SUCCESS: 
    return {loading: false , userInfo: action.payload} ; 

    case USER_SIGININ_FAIL: 
    return {loading: false , error: action.payload} ; 

    case USER_SIGNOUT: 
    return {} ; 


default:
    return state; 
}


};
