import axios from "axios";
import { USER_SIGININ_FAIL, USER_SIGININ_REQUEST, USER_SIGININ_SUCCESS, USER_SIGNOUT } from "../constants/userConstants"

export const signin = (email, password) => async(dispatch) =>{

    dispatch({type: USER_SIGININ_REQUEST, payload:{email, password}});
    try {
const {data} = await axios.post('/api/users/signin', {email, password});
dispatch({type: USER_SIGININ_SUCCESS,payload: data});
localStorage.setItem("userInfo", JSON.stringify(data));

    } catch(error){
        dispatch({type:USER_SIGININ_FAIL,
        payload: error.response && error.response.data.message ? error.response.data.message
        : error.message,
        });
    }
};
export const signout = () => {
    localStorage.removeItem('userInfo');
    localStorage.removeItem('cartItems');
    dispatchEvent({type: USER_SIGNOUT});
}
